# biblioteca
import os

# declarção de lista
nomes = []

# Limpa console
os.system("cls")

# Loop
while True:
    # menu 
    print("1 - Inserir Novo Nome")
    print("2 - Exibir Lista de Nomes")
    print("3 - Excluir nome na Lista")
    print("4 - Sair do Programa")
    opcao = input("Informe a opção desejada: ").strip()
    match opcao:
            case "1":
                os.system("cls")
                novo_nome = input("Informe o Novo Nome: ").strip().title()
                nomes.append(novo_nome) # insere novo nome na lista
                os.system("cls")
                print(f"{novo_nome} cadastrado com sucesso.")
                continue
            case "2":
                os.system("cls")
                print("Lista de nomes:\n")
                for i in range(len(nomes)):
                    print(f"{i} - {nomes[i]}")
                print(f"\n{'-'*40}\n")    
                continue                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
            case "3":
                os.system("cls")
                try:
                    posicao = int(input("Informe a posição a se excluída: ").strip())
                    if posicao >= 0 and posicao < len(nomes):
                        del(nomes[posicao])
                    print("Nome excluido com sucesso.")
    
                    print("Posição Inexistente.")
                except Exception as e:
                    print(f"Posição Inválida, {e}.")
                    continue        
            case "4":
                print("Programa encerrado.")
                break
            case _:
                print("Opção Inválida.")
                continue